import type {
  Address,
  Hash,
  Quantity,
  BlockTag,
  BlockSpec,
} from '../../types/index.js'

/**
 * Returns code at a given address.
 *
 * @example
 * Address: "0xa50a51c09a5c451c52bb714527e1974b686d8e77"
 * Block: "latest"
 * Result: ""
 *
 * Implements the `eth_getCode` JSON-RPC method.
 */

/** The JSON-RPC method name */
export const method = 'eth_getCode' as const

/**
 * Parameters for `eth_getCode`
 */
export interface Params {
  /** hex encoded address */
  address: Address

  /** Block number, tag, or block hash */
  block: BlockSpec
}

/**
 * Result for `eth_getCode`
 *
 * hex encoded bytes
 */
export type Result = Quantity
